@import url('https://fonts.googleapis.com/css2?family=Neuton:wght@400;700;800&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Roboto:wght@300;500;700;900&display=swap');
$header_font: 'Neuton', serif;
$font: 'Roboto', sans-serif; 
$font-color: #1b1a1a;
$accent: rgb(67, 133, 231);
$accent2: rgb(8, 63, 145);
$background-color: white;
// $accent: #4A00C3;

* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

html {
    font-family: $font;
    font-size: 22px;
    line-height: 30px;
    color: $font-color;
}

.container {
    max-width: 90%;
    margin: 0 auto;
}

h1, h2, h3, h4 {
    font-family: $header_font;
}

h1 {
    font-size: 1.4rem;
    margin-bottom: 1rem;
    color: $font-color;
    // -webkit-text-fill-color: white; /* Will override color (regardless of order) */
    // -webkit-text-stroke-width: 1px;
    // -webkit-text-stroke-color: $font-color;
}

.main {
    padding-top: 4rem;
    display: flex;
    flex-direction: column;
}

.desktop-only {
    display: none;
}

.hide-scrollbar {
    /*FireFox*/
    scrollbar-width: none;
    /*IE10+*/
    -ms-overflow-style: -ms-autohiding-scrollbar;
}

.hide-scrollbar::-webkit-scrollbar {
    /*Chrome, Safari, Edge*/
    display: none;
}


@media screen and (min-width: 960px) {
    .mobile-only {
        display: none;
    }

    .desktop-only {
        display: block;
    }

    .main {
        flex-direction: row;
        overflow: hidden;

        .left {
            flex-basis: 50%;
        }

        .right {
            flex-basis: 50%;
        }
    }

    .container {
        max-width: 85%;
    }
}

@media screen and (min-width: 1200px) {
    .container {
        max-width: 75%;
    }

}

sources.scss\\_nav.scss:
nav {
    width: 100%;
    height: 4rem;
    position: fixed;
    padding: .8rem 0 0;
    background-color: white;
    z-index: 10000;

    .navbar {
        display: flex;
        justify-content: space-between;
        align-items: flex-end;

        .logo {
            font-weight: 800;
            font-size: 2.6rem;
            color: $accent2;
            -webkit-text-fill-color: white; /* Will override color (regardless of order) */
            -webkit-text-stroke-width: 1px;
            -webkit-text-stroke-color: $accent;
        }

        .hamburger {
            display: flex;
            flex-direction: column;
            align-items: flex-end;

            .line {
                display: block;
                height: 3px;
                background-color: $accent; 
                margin: 2px 0;

                &:nth-child(1) {
                    width: 34px;                   
                }

                &:nth-child(2) {
                    width: 30px; 
                }

                &:nth-child(3) {
                    width: 26px; 
                }
            }
        }
    }
}


@media screen and (min-width: 960px) { 

    .hamburger {
        display: none !important;
    }
}

