.contact-form {
    margin-top: 2rem;

    form {
        display: flex;
        flex-direction: column;
        align-items: center;
        margin: 2rem 0;

        label {
            color: #686868;
            display: inline-block;
            margin-bottom: .4rem;
        }

        input:not([type="submit"]):not([type="reset"]), textarea {
            width: 100%;
            margin-bottom: 1rem;
            padding: .1rem .4rem;
            font-size: 1rem;
            line-height: 1.6rem;
            font-family: $font;
            font-weight: 300;
            border: 1px solid #4d4d4d;
            border-radius: 3px;

            &[name="message"] {
                height: 8rem;
                resize: vertical;
                text-align: left;
            }

            &::placeholder {
                font-family: $font;
                font-weight: 300;
                color: #a3a3a3;
            }
            
            &:focus {
                border: 2px solid #428fe7;

                &:valid {
                    border: 2px solid rgba(20,181,36,0.75);   
                }
            }

            &:valid {
                border: 2px solid rgba(20,181,36,0.75);   
                box-shadow: none;    
            }

            &:required {
                box-shadow: none;
            }
        }

        .form-buttons {
            width: 100%;

            input[type="reset"], input[type="submit"] {
                display: inline-block;
                padding: .3rem 2rem;
                height: 2rem;
                width: 100%;
                text-align: center;
                text-transform: uppercase;
                text-decoration: none;
                font-size: .8rem;
                color: #fafafa;
                cursor: pointer;
                border: none;
                font-weight: 700;
            }
        
            input[type="reset"] {
                background-color: $accent2;
                margin-top: .6rem;
            }
        
            input[type="submit"] {
                background-color: $accent;
            }
        }
    }

}

@media screen and (min-width: 960px) {
    div.contact-form {
        max-width: 90%;
        margin-top: 1rem;
        height: 50%;

        .contact-header {
            display: none;

        }

        .name-and-email {
            display: flex;
            flex-direction: row;
            justify-content: space-between;
            width: 100%;

            .name-form-group, .email-form-group  {
                flex-basis: 48%;
            }
        }

        .message-form-group {
            width: 100%;

            textarea {
                height: 5rem;
            }
        }

        .form-buttons {
            display: flex;
            width: 100%;
            justify-content: space-between;

            input[type="submit"] {
                width: 60%;
            }

            input[type="reset"] {
                width: 38%;
                margin-top: 0;

            }
        }
    }


}

