.contact-form {
    margin-top: 2rem;

    form {
        display: flex;
        flex-direction: column;
        align-items: center;
        margin: 2rem 0;

        input:not([type="submit"]):not([type="reset"]), textarea {
            width: 100%;
            margin-bottom: 1rem;
            padding: .1rem .4rem;
            font-size: 1rem;
            line-height: 1.6rem;
            font-family: $font;
            font-weight: 300;
            border: none;

            &[name="message"] {
                height: 8rem;
                resize: vertical;
                text-align: left;
            }

            &::placeholder {
                font-family: $font;
                font-weight: 300;
            }

            &:invalid {
                border-bottom: 2px solid #D85959;   
                box-shadow: none;       
            }

            &:valid {
                border-bottom: 2px solid rgba(20,181,36,0.75);   
                box-shadow: none;    
            }            
        }

        input[type="reset"], input[type="submit"] {
            display: inline-block;
            padding: .3rem 2rem;
            height: 2rem;
            width: 100%;
            text-align: center;
            text-transform: uppercase;
            text-decoration: none;
            font-size: .8rem;
            color: #fafafa;
            cursor: pointer;
            border: none;
            font-weight: 700;
        }
    
        input[type="reset"] {
            background-color: #D85959;
            margin-top: .6rem;
        }
    
        input[type="submit"] {
            background-color: $accent;
        }
    }

}

@media screen and (min-width: 960px) {
    div.contact-form {
        max-width: 90%;

        .name-and-email {
            display: flex;
            flex-direction: row;
            justify-content: space-between;
            width: 100%;

            input {
                flex-basis: 48%;
            }
        }
    }


}
